function extend_listview_event(doctype, event, callback) {
    if (!frappe.listview_settings[doctype]) {
        frappe.listview_settings[doctype] = {};
    }

    const old_event = frappe.listview_settings[doctype][event];
    frappe.listview_settings[doctype][event] = function (listview) {
        if (typeof old_event === "function") {
            old_event(listview);
        }
        callback(listview);
    };
}

extend_listview_event("Lead", "refresh", function (listview) {
    if (window.innerWidth > 768) {
        const rows = document.querySelectorAll('.list-row-col');
        const subjects = document.querySelectorAll('.list-subject');
        const mainContainer = document.querySelector('.frappe-list');
        const headers = document.querySelectorAll('.list-row-head, .list-row-container');
        const flexItems = document.querySelectorAll('.list-row, .level-right');

        // Apply styles to .list-row-col elements
        rows.forEach(col => {
            col.style.minWidth = "180px";
            col.style.maxWidth = "180px";
        });

        // Apply styles to .list-subject elements
        subjects.forEach(col => {
            col.style.minWidth = "200px";
            col.style.maxWidth = "200px";
        });

        // Apply styles to main container
        if (mainContainer) {
            mainContainer.style.overflowX = 'auto';
        }

        // Apply styles to headers
        headers.forEach(col => {
            col.style.width = "max-content";
        });

        // Apply styles to flex items
        flexItems.forEach(col => {
            col.style.flex = "auto";
        });
    }
});

