frappe.ui.form.on('Quotation', {
    refresh(frm) {
        // Additional refresh logic (if needed)
    },
    onload(frm) {
        if (frm.doc.opportunity && cur_frm.is_new()) {
            frappe.db.get_value('Opportunity', cur_frm.doc.opportunity, [
                'custom_designing_number', 'contact_person', 'custom_sales_excecutive', 
                'custom_company_name', 'custom_another_mobile_no', 'custom_person_name', 
                "custom_customer_category", "party_name"
            ]).then(r => {
                // Always use contact_person for customer_name
                let baseName = r.message["contact_person"]
                    .replace(/-\d+$/, '')  // Remove any "-n" suffix
                    .replace(/^\w/, c => c.toUpperCase());  // Capitalize first letter
                cur_frm.doc.customer_name = baseName;

                cur_frm.set_value("sales_person", r.message["custom_sales_excecutive"]);
                cur_frm.set_value("custom_company_name", r.message["custom_company_name"]);

                if (r.message["custom_designing_number"] !== null) {
                    frappe.db.get_doc('Designing', r.message["custom_designing_number"])
                        .then(doc => {
                            let data = doc.bom;
                            cur_frm.doc.items = [];
                            for (var i = 0; i < data.length; i++) {
                                var childTable = cur_frm.add_child("items");
                                childTable.item_code = data[i]['item_code'];
                                childTable.item_name = data[i]['item_name'];
                                childTable.item_group = data[i]['item_group'];
                                childTable.description = data[i]['description'];
                                frappe.model.set_value(childTable.doctype, childTable.name, "qty", data[i]['qty']);
                                frappe.model.set_value(childTable.doctype, childTable.name, "uom", data[i]['uom']);

                                childTable.rate = data[i]['rate'];
                                childTable.prevdoc_doctype = 'Opportunity';
                                childTable.prevdoc_docname = cur_frm.doc.opportunity;
                            }
                            cur_frm.refresh_field("items");
                        });
                }
            });
        }

        // Make certain fields read-only for all users except System Manager
        var hasSystemManager = frappe.user.has_role('System Manager');
        if (!hasSystemManager) {
            cur_frm.set_df_property("terms", "read_only", 1);
        }
    },
    payment_terms_template(frm) {
        if (frm.doc.payment_terms_template) {
            frappe.call({
                method: "frappe.client.get",
                args: {
                    doctype: "Payment Terms Template",
                    name: frm.doc.payment_terms_template
                },
                callback: function(r) {
                    if (r.message) {
                        frm.clear_table("payment_schedule");
                        $.each(r.message.terms, function(index, row) {
                            let child = frm.add_child("payment_schedule");
                            frappe.model.set_value(child.doctype, child.name, "payment_term", row.payment_term);
                            frappe.model.set_value(child.doctype, child.name, "due_date", row.due_date);
                            frappe.model.set_value(child.doctype, child.name, "invoice_portion", row.invoice_portion);
                        });
                        frm.refresh_field("payment_schedule");
                    }
                }
            });
        } else {
            frm.clear_table("payment_schedule");
            frm.refresh_field("payment_schedule");
        }
    }
});

