// Employee Advance: Fetch Employee Number based on Logged-In User
frappe.ui.form.on('Employee Advance', {
    refresh(frm) {
        // Ensure the form is in create mode
        if (frm.is_new()) {
            // Fetch the employee number based on the current user ID
            frappe.db.get_value("Employee", {"user_id": frappe.session.user}, ["employee_number"])
                .then(response => {
                    if (response && response.message) {
                        var employee_number = response.message.employee_number;
                        console.log('Fetched employee number:', employee_number); // Debugging log

                        if (employee_number) {
                            // Set the employee number in the form
                            frm.set_value('employee', employee_number);
                        } else {
                            console.log('Employee number not found for the current user.');
                        }
                    } else {
                        console.log('No response or empty response from the server.');
                    }
                })
                .catch(err => {
                    console.error('Error fetching employee number:', err); // Log any errors for debugging
                });
        }
    }
});

// Payment Entry: Fetch Project from Employee Advance
frappe.ui.form.on('Payment Entry', {
    onload: function(frm) {
        if(frm.doc.reference_doctype == "Employee Advance" && frm.doc.reference_name) {
            // Fetch the Employee Advance document
            frappe.db.get_doc('Employee Advance', frm.doc.reference_name)
                .then(employee_advance => {
                    // Set the 'project' field in Payment Entry from 'custom_project' in Employee Advance
                    frm.set_value('project', employee_advance.custom_project);
                });
        }
    }
});

