function calculate_amount(frm, cdt, cdn){
    let row = frappe.get_doc(cdt, cdn);
    if(row.custom_vehicle_type && row.custom_km_travelled){
    	if(row.custom_vehicle_type == "Car"){
    		row.amount = row.custom_car_claimable_amount_per_km * row.custom_km_travelled
    	}
    	if(row.custom_vehicle_type == "Bike"){
    	    row.amount = row.custom_bike_claimable_amount_per_km * row.custom_km_travelled
    	}
    	console.log(row.amount, "Amount")
    	frm.refresh_field("expenses")
    }
}

