function goToQualityInspection(frm) {
    var project_reference = frm.doc.custom_project;
    if (project_reference) {
        frappe.new_doc('Quality Inspection', {
            reference_type: 'Project',
            reference_name: project_reference
        });
    } else {
        frappe.msgprint(__('Please set a project reference first.'));
    }
}

